<script>
import { Carousel, Slide } from 'vue-carousel';

export default {
    name: 'gallery-carosel',
    data () {
      return  {
        isGalleryBrokenImages: false
      }
    },
    components: {
     Carousel,
     Slide
},
  methods: {
    handleBrokenImages: function(e){
      this.isGalleryBrokenImages = true;
      e.target.style.display = 'none'
    }
  }
}
</script>

